class Solution:
    def longestPalindrome(self, s: str) -> int:
        resultCount = len(s)
        checkOdd = 0
        for i in set(s):
            if s.count(i) % 2:
                resultCount -= 1
                checkOdd = 1
        return resultCount + checkOdd

a = Solution()
print(a.longestPalindrome(""))
